// Exercise 1.14
// Draw the tree illustrating the process generated by the count-change procedure of 1.2.2 in making change for 11 cents. What are the orders of growth of the space and number of steps used by this process as the amount to be changed increases?


function countChange (amount) {
    return (function cc (amount, kindsOfCoins) {
        if (amount === 0) {
            return 1;
        } else if (amount < 0 || kindsOfCoins === 0) {
            return 0;
        } else {
            return cc(amount, kindsOfCoins-1) +
                   cc(amount-firstDenomination(kindsOfCoins), kindsOfCoins);
        };
    })(amount, 5);
}

function firstDenomination (kindsOfCoins) {
    switch(kindsOfCoins) {
        case 1: return 1; break;
        case 2: return 5; break;
        case 3: return 10; break;
        case 4: return 25; break;
        case 5: return 50; break;
    };
}

console.log(countChange(11));  // 4


